import React, { useState } from "react";
import { Card, Row } from "react-bootstrap";
import Slider from "./Slider";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHouseUser, faBell, faBolt, faWifi } from "@fortawesome/free-solid-svg-icons";

import "../style/Services.css";

const [index, setIndex] = useState(0);

function Services() {
  return (
    <div className="services">

      <Slider />
      <Row className="d-flex justify-content-center cards">
        <Service icon={faHouseUser}/>
        <Service icon={faBell} />
        <Service icon={faBolt} />
        <Service icon={faWifi} />
      </Row>
    </div>
  );
}

function Service(props) {
  return (
    <div>
      <Card style={{ width: "18rem" }} className="mb-2">
        <Card.Header className="card-header align-center"><FontAwesomeIcon icon={props.icon} /></Card.Header>
        <Card.Body>
          <Card.Title>{props.title}</Card.Title>
          <Card.Text>
            {props.text}
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}

export default Services;
