            <img
              className="d-block w-100"
              src="http://placekitten.com/800/400"
              alt="Third slide"
            />

            <Carousel.Caption>
              <h3>solution connectée</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      </div>
      <Row className="d-flex justify-content-center cards">
        <Service icon={faHouseUser} selected={index === 0} title="Domotique" text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus aliquid quos odit quo debitis dolore, eos beatae a eligendi aut? Fugiat possimus et aperiam cupiditate reprehenderit? Ducimus atque magnam maiores?" />
