        <Service icon={faBolt} selected={index === 2} title="Domotique" text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus aliquid quos odit quo debitis dolore, eos beatae a eligendi aut? Fugiat possimus et aperiam cupiditate reprehenderit? Ducimus atque magnam maiores?" />
        <Service icon={faWifi} selected={index === 3} title="Domotique" text="Lorem ipsum dolor sit amet consectetur adipisicing elit. Accusamus aliquid quos odit quo debitis dolore, eos beatae a eligendi aut? Fugiat possimus et aperiam cupiditate reprehenderit? Ducimus atque magnam maiores?" />
      </Row>
    </div>
  );
}

function Service(props) {
  return (
    <div>
      <Card style={{ width: "18rem" }} className="mb-2">
        <Card.Header className={["card-header align-center"]}>
          <CSSTransition {...transitionStyles} active={props.selected}>
            <FontAwesomeIcon icon={props.icon} />{" "}
          </CSSTransition>
        </Card.Header>
        <Card.Body>
          <Card.Title>{props.title}</Card.Title>
          <Card.Text>{props.text}</Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
}

const transitionStyles = {
  defaultStyle: {
    color: "black",
  },
  enterStyle: {
    color: transit("orange", 500, "ease-in-out"),
  },
  leaveStyle: {
    color: transit("black", 500, "ease-in-out"),
  },
  activeStyle: {
    color: "orange",
  },
};

export default Services;
